import lombok.With;

/**
 * Test class for renaming fields in a class with @With annotation
 */
public class RenameFieldWithWithAnnotation {
  @With
  private final String oldField;
  private int count;

  public RenameFieldWithWithAnnotation(String oldField, int count) {
    this.oldField = oldField;
    this.count = count;
  }

  public void printInfo() {
    System.out.println("Field value: " + oldField + ", Count: " + count);
  }

  public void useWithMethod() {
    // Using the with method generated by Lombok
    RenameFieldWithWithAnnotation updated = withOldField("new value");
    System.out.println("Updated field: " + updated.oldField);
  }
}