class PrimeNumbers {
    public static void main(String[] args) {
        System.out.println("Prime numbers between 1 and 100");

        for (int i = 2; i < 100; i++) {
            boolean isPrime = true;

            for (int j = 2; j < i; j++)

            if (isPrime) {
                System.out.print(i + " ");
            }
        }
    }
}
