package com.jetbrains.python.spellchecker;

import com.intellij.spellchecker.BundledDictionaryProvider;


public final class PythonBundledDictionaryProvider implements BundledDictionaryProvider {
  @Override
  public String[] getBundledDictionaries() {
    return new String[] {
      "python.dic", // autogenerated from python stdlib
      "django.dic",

      // manually added
      "acronyms.dic",
      "logging.dic",
      "pytest.dic",
      "pythonExtras.dic",
      "sqlalchemy.dic",
      "tox.dic",
    };
  }
}
